function palindrome(str) {
  // Good luck!
  str = str.replace(/[^a-zA-Z\d]/g, "").toLowerCase();
  let length = str.length;
  for (let i = 0; i < Math.floor(str.length/2); i++) {
    if (str[i] !== str[length - 1 - i]) {
      return false;
    }
  }
  return true;
}



